    var MaxX = naselje.x + 3;
    var MinY = naselje.y - 3;
    var MaxY = naselje.y + 3;

    var Oaze25 = 0;
    var Oaze50 = 0;
    var OazeBonus = 0;
    var Naslov = unsafeWindow.http+"://" + unsafeWindow.SERVERLINK + "/ajax.php?f=k7&x=" + MinX + "&y=" + MinY + "&xx=" + MaxX + "&yy=" + MaxY;
