function ArrayToString(array) {
    var string = "";
    if (array instanceof Array) {
        string += "[";
        for (var i = 0; i < array.length; i++) {
            if (i != 0) { string += ","; }
            if (array[i] instanceof Array) {
                string += ArrayToString(array[i])
            }
            else if (typeof (array[i]) == "string") {
                string += '"' + array[i] + '"';
            }
            else {
                string += array[i];
            }

        }
        string += "]";
    }
    return string;
}

