function convertToNumber(numberstring) {
    var newnumber = "";
    for (var i = 0; i < numberstring.length; i++) {
        if (numberstring[i] * 1 + "" != "NaN" || numberstring[i] == "-") {
            newnumber += numberstring[i];
        }
    }
    return newnumber * 1;
}


